    private void displayProducts(List<Product> products) {
        StringBuilder builder = new StringBuilder();
        builder.append("<html><body><table border='1' style='width:100%'>");
        builder.append("<tr><th>ID</th><th>Nazwa</th><th>Cena</th><th>Ilość</th><th>Kategoria</th><th>Typ</th><th>Waga</th><th>Gwarancja</th><th>Materiał</th></tr>");
        for(Product product : products){
            builder.append("<tr>");
            builder.append("<td>").append(product.getId()).append("</td>");
            builder.append("<td>").append(product.getNazwa()).append("</td>");
            builder.append("<td>").append(product.getCena()).append("</td>");
            builder.append("<td>").append(product.getIlosc()).append("</td>");
            builder.append("<td>").append(product.getKategoria()).append("</td>");
            builder.append("<td>").append(product.getTyp()).append("</td>");
            builder.append("<td>").append(product.getWaga()).append("</td>");
            builder.append("<td>").append(product.getGwarancja()).append("</td>");
            builder.append("<td>").append(product.getMaterial()).append("</td>");
            builder.append("</tr>");
        }
        builder.append("</table></body></html>");
        productTable.setText(builder.toString());
    }
