public class WarehouseOrderDAO {
    public void createWarehouseOrder(int productId, int quantity, String supplier) throws SQLException {
        String sql = "INSERT INTO zamowienia_magazynowe (produkt_id, ilosc, dostawca, data_zamowienia, zrealizowano) VALUES (?, ?, ?, NOW(), false)";

        try (Connection conn = DatabaseConnection.getConnection();
