import java.sql.*;
import java.util.ArrayList;
import java.util.List;

public class WarehouseOrderDAO {
    public void createWarehouseOrder(int productId, int quantity, String supplier) throws SQLException {
        String sql = "INSERT INTO zamowienia_magazynowe (produkt_id, ilosc, dostawca, data_zamowienia, zrealizowano) VALUES (?, ?, ?, NOW(), false)";

        try (Connection conn = DatabaseConnection.getConnection();
             PreparedStatement stmt = conn.prepareStatement(sql, Statement.RETURN_GENERATED_KEYS)) {

            stmt.setInt(1, productId);
            stmt.setInt(2, quantity);
            stmt.setString(3, supplier);
            stmt.executeUpdate();
        }
    }

    public List<WarehouseOrder> getAllWarehouseOrders() throws SQLException {
        List<WarehouseOrder> orders = new ArrayList<>();
        String sql = "SELECT * FROM zamowienia_magazynowe ORDER BY data_zamowienia DESC";

        try (Connection conn = DatabaseConnection.getConnection();
             PreparedStatement stmt = conn.prepareStatement(sql);
             ResultSet rs = stmt.executeQuery()) {

            while (rs.next()) {
                WarehouseOrder order = new WarehouseOrder(
                        rs.getInt("id"),
                        rs.getInt("produkt_id"),
                        rs.getInt("ilosc"),
                        rs.getTimestamp("data_zamowienia"), // Timestamp is a subclass of Date, so this is compatible
                        rs.getString("dostawca"),
                        rs.getBoolean("zrealizowano")
                );
                orders.add(order);
            }
        }
        return orders;
    }

    public void markOrderAsCompleted(int orderId) throws SQLException {
        String sql = "UPDATE zamowienia_magazynowe SET zrealizowano = true WHERE id = ?";

        try (Connection conn = DatabaseConnection.getConnection();
