    private void addToCart() {
        int selectedRow = productsTable.getSelectedRow();
        if(selectedRow >=0){
            Product product = productsTableModel.getProductAt(selectedRow);
            int quantity = (int)quantitySpinner.getValue();
            if(quantity>product.getIlosc()){
                JOptionPane.showMessageDialog(this, "Wybrana ilość jest większa niż dostępna w magazynie!", "Błąd", JOptionPane.ERROR_MESSAGE);
                return;
            }
            koszyk.dodajProdukt(product,quantity);
            cartTableModel.fireTableDataChanged();
        }else{
            JOptionPane.showMessageDialog(this,"Nie wybrano produktu!","Błąd",JOptionPane.ERROR_MESSAGE);
        }
    }

    private void placeOrder() {
        if (koszyk.getProdukty().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Koszyk jest pusty!",
                    "Błąd", JOptionPane.ERROR_MESSAGE);
            return;
        }

        try {
            // Pobierz dane klienta z bazy
            // String customerEmail = ...;

            // Złóż zamówienie używając danych z bazy
            OrderDAO.saveOrder(koszyk, customerId);

            // Aktualizuj saldo sklepu
            updateStoreBalance();

            // Wyświetl potwierdzenie
            showOrderConfirmation();

            // Wyczyść koszyk
            koszyk.getProdukty().clear();
            cartTableModel.fireTableDataChanged();
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Błąd podczas składania zamówienia: " + e.getMessage(),
                    "Błąd", JOptionPane.ERROR_MESSAGE);
        }
    }

    private void showOrderConfirmation() {
        StringBuilder message = new StringBuilder();
        message.append("Zamówienie zostało złożone!\n\n");
        message.append("Produkty:\n");

        for (Map.Entry<Product, Integer> entry : koszyk.getProdukty()) {
            message.append(entry.getKey().getNazwa())
                    .append(" X ")
                    .append(entry.getValue())
                    .append(" - ")
                    .append(String.format("%.2f zł\n", entry.getKey().getCena() * entry.getValue()));
        }

    }

    private void updateStoreBalance() throws SQLException{
        double totalAmount = koszyk.obliczCalkowitaSume();
        String query = "UPDATE finanse_sklepu SET saldo = saldo + ? WHERE id = 1";
        try(Connection conn = DatabaseConnection.getConnection();
            PreparedStatement stmt = conn.prepareStatement(query)){
            stmt.setDouble(1,totalAmount);
            stmt.executeUpdate();
        }
    }

    private void editCustomerData() {
        AddEditWholeSaleCustomer editDialog = new AddEditWholeSaleCustomer(this,customerId);
        editDialog.setVisible(true);
        refreshCustomerData();

    }

    private void refreshCustomerData() {
        customerDataPanel.removeAll();
        loadCustomerData();
        customerDataPanel.revalidate();
        customerDataPanel.repaint();
    }

