    private void styleDateSpinners() {
        // Stylizacja spinnerów daty
        fromDateSpinner.setFont(new Font("Segoe UI", Font.PLAIN, 14));
        toDateSpinner.setFont(new Font("Segoe UI", Font.PLAIN, 14));

        // Ustawienie rozmiaru spinnerów
        Dimension spinnerSize = new Dimension(120, 30);
        fromDateSpinner.setPreferredSize(spinnerSize);
        toDateSpinner.setPreferredSize(spinnerSize);

        // Format daty
        JSpinner.DateEditor fromEditor = new JSpinner.DateEditor(fromDateSpinner, "yyyy-MM-dd");
        JSpinner.DateEditor toEditor = new JSpinner.DateEditor(toDateSpinner, "yyyy-MM-dd");
        fromDateSpinner.setEditor(fromEditor);
        toDateSpinner.setEditor(toEditor);

        // Dodanie listenerów do otwierania kalendarza po kliknięciu
        fromEditor.getTextField().addMouseListener(new MouseAdapter() {
            @Override
            public void mouseClicked(MouseEvent e) {
                fromDateSpinner.requestFocus();
            }
        });

        toEditor.getTextField().addMouseListener(new MouseAdapter() {
            @Override
            public void mouseClicked(MouseEvent e) {
                toDateSpinner.requestFocus();
            }
        });
    }

