public class ShopWholeSaleForm extends JFrame {
    private JPanel Panel1;
    private JPanel baner;
    private JLabel Logo;
    private JLabel Napis;
    private JPanel bottomPanel;
    private JPanel mainPanel;
    private JPanel customerDataPanel;
