    private JButton placeOrderButton;
    private JButton addToCartButton;
    private JPanel cartPanel;
    private JSpinner quantitySpinner;
    private JPanel productsPanel;
    private JTable productsTable;
    private JTable cartTable;
    private JLabel statusLabel;
    private JPanel cartButtonsPanel;
    private Cart koszyk;
    private KoszykTableModel cartTableModel;
    private ProductTableModel productsTableModel;
    private int customerId;

    public ShopWholeSaleForm(int customerId){
        this.customerId = customerId;
        this.koszyk = new Cart();
        this.setTitle("Panel Klienta Hurtowego");
        this.setSize(1000, 700);
        this.setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);
        this.setLocationRelativeTo(null);
        this.setContentPane(this.Panel1);

        initializeComponents();
        loadCustomerData();
        loadProducts();
        setupTimer();
    }

    private void setupTimer() {
        new Timer(1000, new ActionListener() {
            @Override
            public void actionPerformed(ActionEvent e) {
                statusLabel.setText("Użytkownik: Klient hurtowy | Data: " + new Date().toString());
            }
        }).start();
    }

    private void loadProducts() {
        try {
            ProductsDAO dao = new ProductsDAO();
            List<Product> products = dao.getAllProducts();
            productsTableModel.setProducts(products);
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Błąd ładowania produktów: " + e.getMessage(), "Błąd", JOptionPane.ERROR_MESSAGE);
            e.printStackTrace();
        }
    }

    private void initializeComponents() {
        ImageIcon icon = new ImageIcon(getClass().getResource("/figurs/logo.png"));
        setIconImage(icon.getImage());

        customerDataPanel.setBorder(BorderFactory.createTitledBorder("Twoje dane"));
        editDataButton = new JButton("Edytuj dane");
        editDataButton.addActionListener(e->editCustomerData());
        customerDataPanel.add(editDataButton);

        cartPanel.setBorder(BorderFactory.createTitledBorder("Koszyk"));
        cartTableModel = new KoszykTableModel(koszyk);
        cartTable.setModel(cartTableModel);

        placeOrderButton = new JButton("Złóż zamówienie");
        placeOrderButton.addActionListener(e->placeOrder());

        cartButtonsPanel.add(placeOrderButton);

        productsPanel.setBorder(BorderFactory.createTitledBorder("Lista produktów"));
        productsTableModel = new ProductTableModel();
        productsTable.setModel(productsTableModel);
        productsTable.setSelectionMode(ListSelectionModel.SINGLE_SELECTION);

        quantitySpinner = new JSpinner(new SpinnerNumberModel(1,1,100,1));
        addToCartButton = new JButton("Dodaj do koszyka");
        addToCartButton.addActionListener(e -> addToCart());

        JPanel productsControlPanel = new JPanel();
        productsControlPanel.add(new JLabel("Ilość: "));
        productsControlPanel.add(quantitySpinner);
        productsControlPanel.add(addToCartButton);

        productsPanel.add(productsControlPanel, BorderLayout.SOUTH);
    }

    private void loadCustomerData() {
        String sql = "SELECT * FROM klienci_hurtowi WHERE id = ?";
        try(Connection conn = DatabaseConnection.getConnection();
            PreparedStatement stmt = conn.prepareStatement(sql)){
            stmt.setInt(1,customerId);
            ResultSet rs = stmt.executeQuery();

            if (rs.next()) {
                customerDataPanel.add(new JLabel("Imię:"));
                customerDataPanel.add(new JLabel(rs.getString("imie")));
                customerDataPanel.add(new JLabel("Nazwisko:"));
                customerDataPanel.add(new JLabel(rs.getString("nazwisko")));
                customerDataPanel.add(new JLabel("Firma:"));
                customerDataPanel.add(new JLabel(rs.getString("nazwa_firmy")));
                customerDataPanel.add(new JLabel("NIP:"));
                customerDataPanel.add(new JLabel(rs.getString("NIP")));
                customerDataPanel.add(new JLabel("Adres:"));
                customerDataPanel.add(new JLabel(rs.getString("adres")));
                customerDataPanel.add(new JLabel("Telefon:"));
                customerDataPanel.add(new JLabel(rs.getString("telefon")));
                customerDataPanel.add(new JLabel("Email:"));
                customerDataPanel.add(new JLabel(rs.getString("email")));
                customerDataPanel.add(new JLabel("Suma zakupów:"));
                customerDataPanel.add(new JLabel(String.format("%.2f zł", rs.getDouble("suma_zakupow"))));
            }
        }catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Błąd ładowania danych klienta: " + e.getMessage(),
                    "Błąd", JOptionPane.ERROR_MESSAGE);
        }
    }

    private void addToCart() {
        int selectedRow = productsTable.getSelectedRow();
        if(selectedRow >=0){
            Product product = productsTableModel.getProductAt(selectedRow);
            int quantity = (int)quantitySpinner.getValue();
            if(quantity>product.getIlosc()){
                JOptionPane.showMessageDialog(this, "Wybrana ilość jest większa niż dostępna w magazynie!", "Błąd", JOptionPane.ERROR_MESSAGE);
                return;
            }
            koszyk.dodajProdukt(product,quantity);
            cartTableModel.fireTableDataChanged();
        }else{
            JOptionPane.showMessageDialog(this,"Nie wybrano produktu!","Błąd",JOptionPane.ERROR_MESSAGE);
        }
    }

    private void placeOrder() {
        if (koszyk.getProdukty().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Koszyk jest pusty!",
                    "Błąd", JOptionPane.ERROR_MESSAGE);
            return;
        }

        try {
            saveWholeSaleOrder();
            updateStoreBalance();
            showOrderConfirmation();
            koszyk.getProdukty().clear();
            cartTableModel.fireTableDataChanged();

        } catch (SQLException e) {
            JOptionPane.showMessageDialog(this, "Błąd podczas składania zamówienia: " + e.getMessage(),
                    "Błąd", JOptionPane.ERROR_MESSAGE);
        }
    }

    private void saveWholeSaleOrder() throws SQLException {
        Connection conn = null;
        try {
            conn = DatabaseConnection.getConnection();
            conn.setAutoCommit(false);
            double totalAmount = koszyk.obliczCalkowitaSume();
            int transactionId = insertWholesaleTransaction(conn, totalAmount);
            insertTransactionItems(conn, transactionId);
            updateClientPurchaseSum(conn, totalAmount);
            conn.commit();
        } catch (SQLException e) {
            if (conn != null) conn.rollback();
            throw e;
        } finally {
            if (conn != null) conn.setAutoCommit(true);
        }
    }

    private int insertWholesaleTransaction(Connection conn, double totalAmount) throws SQLException {
        String sql = "INSERT INTO transakcje (klient_hurtowy_id, data, calkowita_kwota, typ, metoda_platnosci) " +
                "VALUES (?, NOW(), ?, 'hurtowy', 'karta')";
        try (PreparedStatement stmt = conn.prepareStatement(sql, PreparedStatement.RETURN_GENERATED_KEYS)) {
            stmt.setInt(1, customerId);
            stmt.setDouble(2, totalAmount);
            stmt.executeUpdate();

            try (var generatedKeys = stmt.getGeneratedKeys()) {
                if (generatedKeys.next()) {
                    return generatedKeys.getInt(1);
                } else {
                    throw new SQLException("Creating transaction failed, no ID obtained.");
                }
            }
        }
    }

    private void insertTransactionItems(Connection conn, int transactionId) throws SQLException {
        String insertSql = "INSERT INTO pozycje_transakcji (transakcja_id, produkt_id, ilosc, cena_jednostkowa) VALUES (?, ?, ?, ?)";
        String updateSql = "UPDATE produkty SET ilosc = ilosc - ? WHERE id = ?";

        try (PreparedStatement insertStmt = conn.prepareStatement(insertSql);
             PreparedStatement updateStmt = conn.prepareStatement(updateSql)) {

            for (Map.Entry<Product, Integer> entry : koszyk.getProdukty()) {
                Product product = entry.getKey();
                int quantity = entry.getValue();

                // Insert transaction item
                insertStmt.setInt(1, transactionId);
                insertStmt.setInt(2, product.getId());
                insertStmt.setInt(3, quantity);
                insertStmt.setDouble(4, product.getCena());
                insertStmt.addBatch();

                // Update product quantity
                updateStmt.setInt(1, quantity);
                updateStmt.setInt(2, product.getId());
                updateStmt.addBatch();
            }

            insertStmt.executeBatch();
            updateStmt.executeBatch();
        }
    }

    private void updateClientPurchaseSum(Connection conn, double totalAmount) throws SQLException {
        String sql = "UPDATE klienci_hurtowi SET suma_zakupow = suma_zakupow + ? WHERE id = ?";
        try (PreparedStatement stmt = conn.prepareStatement(sql)) {
            stmt.setDouble(1, totalAmount);
            stmt.setInt(2, customerId);
            stmt.executeUpdate();
        }
    }

    private void showOrderConfirmation() {
        StringBuilder message = new StringBuilder();
        message.append("Zamówienie zostało złożone!\n\n");
        message.append("Produkty:\n");

        for (Map.Entry<Product, Integer> entry : koszyk.getProdukty().entrySet()) {
            message.append(entry.getKey().getNazwa())
                    .append(" X ")
                    .append(entry.getValue())
                    .append(" - ")
                    .append(String.format("%.2f zł\n", entry.getKey().getCena() * entry.getValue()));
        }

        message.append("\nŁączna wartość: ").append(String.format("%.2f zł", koszyk.obliczCalkowitaSume()));

        JOptionPane.showMessageDialog(this, message.toString(), "Potwierdzenie zamówienia", JOptionPane.INFORMATION_MESSAGE);
    }

    private void updateStoreBalance() throws SQLException {
        double totalAmount = koszyk.obliczCalkowitaSume();
        String query = "UPDATE finanse_sklepu SET saldo = saldo + ? WHERE id = 1";
        try(Connection conn = DatabaseConnection.getConnection();
            PreparedStatement stmt = conn.prepareStatement(query)){
            stmt.setDouble(1,totalAmount);
            stmt.executeUpdate();
        }
    }

    private void editCustomerData() {
        AddEditWholeSaleCustomer editDialog = new AddEditWholeSaleCustomer(this,customerId);
        editDialog.setVisible(true);
        refreshCustomerData();
    }

    private void refreshCustomerData() {
        customerDataPanel.removeAll();
        loadCustomerData();
        customerDataPanel.revalidate();
        customerDataPanel.repaint();
    }
